<script>
	import { tileLayer } from "leaflet/src/layer/tile/TileLayer.js";
	import { getContext, onDestroy } from "svelte";

	export let url;
	export let options = {};

	let map = getContext("leafletMapInstance");

	let layer = tileLayer(url, options).addTo(map);

	onDestroy(() => layer.remove());
</script>
